import React, { useState, useEffect } from 'react';

const UserDashboard = ({ username }) => {
    const [structuredData, setStructuredData] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchBooks = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/auth/books');
                if (response.ok) {
                    const data = await response.json();
                    setStructuredData(data); // Update state with structured data
                } else {
                    setError('Failed to fetch books');
                }
            } catch (error) {
                setError('Failed to fetch books: ' + error.message);
            }
        };

        fetchBooks();
    }, []);

    const handleLogout = async () => {
        try {
            await fetch('http://localhost:5000/api/auth/logout', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username }),
            });
            // Redirect to login page after successful logout
            window.location.href = '/login';
        } catch (error) {
            console.error('Logout failed:', error);
        }
    };

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <h2>Welcome to User Dashboard</h2>
            <button onClick={handleLogout}>Logout</button>

            <div className="structured-book-list">
                {Object.keys(structuredData).map(publisherName => (
                    <div key={publisherName}>
                        <h3>{publisherName}</h3>
                        {structuredData[publisherName] && Object.keys(structuredData[publisherName]).map(authorName => (
    <div key={authorName}>
        <h4>{authorName}</h4>
        <div className="book-list">
            {Array.isArray(structuredData[publisherName][authorName]) &&
                structuredData[publisherName][authorName].map(book => (
                    <div key={book._id} className="book-card">
                                            <h5>{book.bookName}</h5>
                                            <p><strong>Author:</strong> {authorName}</p>
                                            <p><strong>Publisher Date:</strong> {new Date(book.publisherDate).toLocaleDateString()}</p>
                                            <p><strong>Description:</strong> {book.description}</p>
                                            <p><strong>Total Copies:</strong> {book.totalCopies}</p>
                                            <img src={book.imgUrl} alt={book.bookName} className="book-image" />
                                        </div>
                                    ))}
                                </div>
                            </div>
                        ))}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default UserDashboard;
