import React, { useState, useEffect } from 'react';
import './AdminDashboard.css';
import { useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null);

  const [book, setBook] = useState({
    bookName: '',
    authorName: '',
    publisherName: '',
    publisherDate: '',
    totalCopies: '',
    imgUrl: '',
    description: ''
  });

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/auth/users');
        if (response.ok) {
          const data = await response.json();
          const filteredUsers = data.filter(user => user.role !== 'admin');
          setUsers(filteredUsers);
        } else {
          console.error('Failed to fetch users');
        }
      } catch (error) {
        console.error('Failed to fetch users:', error);
      }
    };

    fetchUsers();
  }, []);

  const handleAddBook = async (e) => {
    e.preventDefault();

    const bookDetails = {
      bookName: book.bookName,
      imgUrl: book.imgUrl,
      description: book.description,
      publisherDate: new Date(book.publisherDate),
      totalCopies: parseInt(book.totalCopies),
      purchasedCopies: 0,
    };

    const data = {
      publisherName: book.publisherName,
      authorName: book.authorName,
      bookDetails,
    };

    try {
      const response = await fetch('http://localhost:5000/api/auth/books', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });
      if (response.ok) {
        alert('Book added successfully');
        setBook({
          bookName: '',
          authorName: '',
          publisherName: '',
          publisherDate: '',
          totalCopies: '',
          imgUrl: '',
          description: ''
        });
      } else {
        console.error('Failed to add book');
      }
    } catch (error) {
      console.error('Failed to add book:', error);
    }
  };

  const handleBookChange = (e) => {
    const { name, value } = e.target;
    setBook({ ...book, [name]: value });
  };

  const handleLogout = () => {
    window.location.href = '/login';
  };

  const navigate = useNavigate();

  const handleViewBooks = () => {
    navigate('/view-books');
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
  };

  const handleDeleteUser = async (userId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/auth/users/${userId}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        setUsers(users.filter(user => user._id !== userId));
      } else {
        console.error('Failed to delete user');
      }
    } catch (error) {
      console.error('Failed to delete user:', error);
    }
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/api/auth/users/${editingUser._id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(editingUser),
      });
      if (response.ok) {
        const updatedUser = await response.json();
        setUsers(users.map(user => (user._id === editingUser._id ? updatedUser : user)));
        setEditingUser(null);
      } else {
        console.error('Failed to update user');
      }
    } catch (error) {
      console.error('Failed to update user:', error);
    }
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditingUser({ ...editingUser, [name]: value });
  };

  return (
    <div className="admin-dashboard">
      <h1>Welcome to Admin Dashboard</h1>

      <button className="logout-button" onClick={handleLogout}>Logout</button>
      <button className="view-books-button" onClick={handleViewBooks}>View Books</button>

      <h2>User List</h2>
      <table className="user-table">
        <thead>
          <tr>
            <th>Full Name</th>
            <th>Username</th>
            <th>Email</th>
            <th>Phone Number</th>
            <th>Login Date</th>
            <th>Login Time</th>
            <th>Logout Time</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user._id}>
              <td>{user.fullName}</td>
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>{user.mobile}</td>
              <td>{user.lastLogin ? new Date(user.lastLogin).toLocaleDateString() : 'N/A'}</td>
              <td>{user.loginTime ? user.loginTime : 'N/A'}</td>
              <td>{user.logoutTime ? user.logoutTime : 'N/A'}</td>
              <td>
                <button onClick={() => handleEditUser(user)}>Edit</button>
                <button onClick={() => handleDeleteUser(user._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editingUser && (
        <form className="edit-user-form" onSubmit={handleEditSubmit}>
          <h3>Edit User</h3>
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={editingUser.username}
            onChange={handleEditChange}
            required
          />
          <input
            type="text"
            name="loginTime"
            placeholder="Login Time"
            value={editingUser.loginTime}
            onChange={handleEditChange}
            required
          />
          <input
            type="text"
            name="logoutTime"
            placeholder="Logout Time"
            value={editingUser.logoutTime}
            onChange={handleEditChange}
            required
          />
          <button type="submit">Save</button>
          <button type="button" onClick={() => setEditingUser(null)}>Cancel</button>
        </form>
      )}

      <div className="add-book-form">
        <h2>Add Book</h2>
        <form onSubmit={handleAddBook}>
          <input type="text" name="bookName" placeholder="Book Name" value={book.bookName} onChange={handleBookChange} required />
          <input type="text" name="authorName" placeholder="Author Name" value={book.authorName} onChange={handleBookChange} required />
          <input type="text" name="publisherName" placeholder="Publisher Name" value={book.publisherName} onChange={handleBookChange} required />
          <input type="date" name="publisherDate" placeholder="Publication Date" value={book.publisherDate} onChange={handleBookChange} required />
          <input type="number" name="totalCopies" placeholder="Total Copies" value={book.totalCopies} onChange={handleBookChange} required />
          <input type="text" name="imgUrl" placeholder="Image URL" value={book.imgUrl} onChange={handleBookChange} required />
          <textarea name="description" placeholder="Description" value={book.description} onChange={handleBookChange} required />
          <button type="submit">Add Book</button>
        </form>
      </div>
    </div>
  );
};

export default AdminDashboard;
