import React, { useEffect, useState } from 'react';

const UserDashboard = () => {
  const [publishers, setPublishers] = useState([]);

  useEffect(() => {
    const fetchPublishers = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/auth/publishers');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setPublishers(data);
      } catch (error) {
        console.error('Failed to fetch publishers:', error);
      }
    };

    fetchPublishers();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <div className="flex overflow-x-auto space-x-4">
        {publishers.map((publisher) => (
          <div key={publisher._id} className="w-full md:w-1/2 lg:w-1/3 flex-shrink-0">
            <div className="bg-white p-6 rounded-lg shadow-lg">
              <h2 className="text-xl font-bold mb-2">{publisher.publisherName}</h2>
              {publisher.authors.map((author) => (
                <div key={author._id} className="mt-4">
                  <h3 className="text-lg font-semibold">Author: {author.authorName}</h3>
                  <div className="grid grid-cols-1 gap-4 mt-2">
                    {author.books.map((book) => (
                      <div key={book._id} className="bg-gray-100 p-4 rounded-lg">
                        <img
                          src={book.imgUrl}
                          alt={book.bookName}
                          className="w-full h-48 object-cover rounded-t-lg"
                        />
                        <div className="p-4">
                          <h4 className="text-md font-semibold">{book.bookName}</h4>
                          <p className="text-sm text-gray-700">{book.description}</p>
                          <p className="text-sm text-gray-600 mt-2">
                            Published on: {new Date(book.publisherDate).toLocaleDateString()}
                          </p>
                          <p className="text-sm text-gray-600">
                            Total Copies: {book.totalCopies}
                          </p>
                          <p className="text-sm text-gray-600">
                            Purchased Copies: {book.purchasedCopies}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default UserDashboard;
