import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const UserDashboard = ({ username }) => {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [selectedBook, setSelectedBook] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/auth/books');
        if (response.ok) {
          const data = await response.json();
          setBooks(data.map(book => ({ ...book, count: 0 }))); // Initialize count to 0
          setFilteredBooks(data); // Initialize filteredBooks with the fetched data
        } else {
          console.error('Failed to fetch books');
        }
      } catch (error) {
        console.error('Failed to fetch books:', error);
      }
    };

    fetchBooks();
  }, []);

  useEffect(() => {
    const results = books.filter(book =>
      book.bookName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      book.authorName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      book.publisherName.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredBooks(results);
  }, [searchQuery, books]);

  const handleLogout = async () => {
    try {
      await fetch('http://localhost:5000/api/auth/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username }),
      });
      // Display a message indicating successful logout
      alert('Logout successful');
      // Redirect to login page after successful logout
      window.location.href = '/login';
    } catch (error) {
      console.error('Logout failed:', error);
    }
  };

  const handleViewMore = (book) => {
    setSelectedBook(book);
  };

  const handleAddToFavorites = (book) => {
    const favorites = JSON.parse(localStorage.getItem('favorites')) || [];
    favorites.push(book);
    const updatedBooks = books.map(b => {
      if (b._id === book._id) {
        return { ...b, count: b.count + 1 }; // Increment count
      }
      return b;
    });
    setBooks(updatedBooks);
    localStorage.setItem('favorites', JSON.stringify(favorites));
  };

  const handleBuyNow = async (book) => {
    try {
      const response = await fetch(`http://localhost:5000/api/auth/books/purchase/${book._id}`, {
        method: 'POST'
      });
      if (response.ok) {
        const updatedBooks = books.map(b => {
          if (b._id === book._id) {
            return { ...b, noOfCopies: b.noOfCopies - 1 }; // Update the local state with decreased copies
          }
          return b;
        });
        setBooks(updatedBooks);
        alert(`Book purchased: ${book.bookName}`);
      } else {
        console.error('Failed to purchase book');
      }
    } catch (error) {
      console.error('Failed to purchase book:', error);
    }
  };

  const navigate = useNavigate();

  const handleViewWishlist = () => {
    navigate('/wishlist');
  };

  const closeModal = () => {
    setSelectedBook(null);
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-3xl font-bold mb-6 text-indigo-600">Welcome to User Dashboard</h2>
      <div className="flex justify-between mb-4">
        <input 
          type="text" 
          placeholder="Search by book name, author, or publisher" 
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="w-1/2 p-2 border rounded"
        />
        <div className="space-x-2">
          <button 
            onClick={handleLogout} 
            className="bg-red-500 text-white px-4 py-1 rounded hover:bg-red-700 transition duration-300"
          >
            Logout
          </button>
          <button 
            onClick={handleViewWishlist} 
            className="bg-blue-500 text-white px-4 py-1 rounded hover:bg-blue-700 transition duration-300"
          >
            View Wishlist
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {filteredBooks.map((book) => (
          <div 
            key={book._id} 
            className="bg-white shadow-lg rounded-lg p-4 flex flex-col items-center h-full transition-transform transform hover:scale-105 hover:shadow-2xl"
          >
            <img 
              src={book.bookImgUrl} 
              alt={book.bookName} 
              className="h-40 w-full object-contain mb-4 rounded"
            />
            <div className="flex-grow flex flex-col justify-between w-full">
              <div className="text-center">
                <h4 className="font-semibold text-lg mb-2 text-indigo-700">{book.bookName}</h4>
                <p className="text-gray-600 mb-1">{book.authorName}</p>
                <p className="text-gray-600 mb-1">{new Date(book.publishingDate).toLocaleDateString()}</p>
                <p className="text-gray-600 mb-1">No. of Copies: {book.noOfCopies}</p>
                <p className="text-gray-600 mb-1">Added to Favorites: {book.count || 0}</p>
              </div>
              <div className="flex justify-between mt-4 w-full">
                <button 
                  onClick={() => handleViewMore(book)} 
                  className="bg-indigo-500 text-white w-1/2 py-1 rounded hover:bg-indigo-700 transition duration-300 mr-1"
                >
                  View More
                </button>
                <button 
                  onClick={() => handleAddToFavorites(book)} 
                  className="bg-green-500 text-white w-1/2 py-1 rounded hover:bg-green-700 transition duration-300 ml-1"
                >
                  Add Favorites
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {selectedBook && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div className="bg-white rounded-lg p-6 w-3/4 md:w-1/2 lg:w-1/3 relative">
            <span 
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-700 cursor-pointer"
              onClick={closeModal}
            >
              &times;
            </span>
            <h2 className="text-2xl font-bold mb-4 text-indigo-700">{selectedBook.bookName}</h2>
            <p className="mb-2"><strong>Author:</strong> {selectedBook.authorName}</p>
            <p><strong>Description:</strong> {selectedBook.description}</p>
            <div className="flex justify-end mt-4">
              <button 
                onClick={() => handleBuyNow(selectedBook)} 
                className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-700 transition duration-300"
              >
                Buy Now
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
