// ViewBooks.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ViewBooks = () => {
  const [purchasedBooks, setPurchasedBooks] = useState([]);
  const [editedBook, setEditedBook] = useState(null);

  useEffect(() => {
    const fetchPurchasedBooks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/auth/purchased-books');
        setPurchasedBooks(response.data);
      } catch (error) {
        console.error('Failed to fetch purchased books:', error);
      }
    };

    fetchPurchasedBooks();
  }, []);

  const handleEditClick = (book) => {
    setEditedBook({ ...book });
  };

  const handleSaveClick = async () => {
    try {
      const { _id, bookName, totalCopies, purchasedCopies } = editedBook;
      const response = await axios.put(`http://localhost:5000/api/auth/books/${_id}`, {
        bookName,
        totalCopies,
        purchasedCopies,
      });

      const updatedBook = response.data;

      setPurchasedBooks((prevBooks) =>
        prevBooks.map((book) => (book._id === updatedBook._id ? updatedBook : book))
      );

      setEditedBook(null);
    } catch (error) {
      console.error('Failed to update book:', error);
    }
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditedBook({ ...editedBook, [name]: value });
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-3xl font-bold mb-6 text-indigo-600">Purchased Books</h2>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-gray-800 text-white border border-gray-600">
          <thead>
            <tr>
              <th className="py-3 px-6 border-b-2 border-gray-600">Book Name</th>
              <th className="py-3 px-6 border-b-2 border-gray-600">Publication Date</th>
              <th className="py-3 px-6 border-b-2 border-gray-600">Total Copies</th>
              <th className="py-3 px-6 border-b-2 border-gray-600">Purchased Copies</th>
              <th className="py-3 px-6 border-b-2 border-gray-600">Author</th>
              <th className="py-3 px-6 border-b-2 border-gray-600">Publisher</th>
              <th className="py-3 px-6 border-b-2 border-gray-600">Actions</th>
            </tr>
          </thead>
          <tbody>
            {purchasedBooks.map((book) => (
              <tr key={book._id} className="hover:bg-gray-700">
                <td className="py-3 px-6 border-b border-gray-600">{book.bookName}</td>
                <td className="py-3 px-6 border-b border-gray-600">
                  {new Date(book.publisherDate).toLocaleDateString()}
                </td>
                <td className="py-3 px-6 border-b border-gray-600">{book.totalCopies}</td>
                <td className="py-3 px-6 border-b border-gray-600">{book.purchasedCopies}</td>
                <td className="py-3 px-6 border-b border-gray-600">{book.authorName}</td>
                <td className="py-3 px-6 border-b border-gray-600">{book.publisherName}</td>
                <td className="py-3 px-6 border-b border-gray-600">
                  <button
                    onClick={() => handleEditClick(book)}
                    className="bg-blue-500 text-white px-2 py-1 rounded hover:bg-blue-700 transition duration-300 mr-2"
                  >
                    Edit
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {editedBook && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div className="bg-white rounded-lg p-6 w-3/4 md:w-1/2 lg:w-1/3 relative">
            <h2 className="text-2xl font-bold mb-4 text-indigo-700">Edit Book</h2>
            <label className="block mb-2">
              <span className="text-gray-700">Book Name</span>
              <input
                type="text"
                name="bookName"
                value={editedBook.bookName}
                onChange={handleEditChange}
                className="w-full p-2 border rounded"
              />
            </label>
            <label className="block mb-2">
              <span className="text-gray-700">Total Copies</span>
              <input
                type="number"
                name="totalCopies"
                value={editedBook.totalCopies}
                onChange={handleEditChange}
                className="w-full p-2 border rounded"
              />
            </label>
            <label className="block mb-2">
              <span className="text-gray-700">Purchased Copies</span>
              <input
                type="number"
                name="purchasedCopies"
                value={editedBook.purchasedCopies}
                onChange={handleEditChange}
                className="w-full p-2 border rounded"
              />
            </label>
            <div className="flex justify-end mt-4">
              <button
                onClick={handleSaveClick}
                className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-700 transition duration-300"
              >
                Save
              </button>
              <button
                onClick={() => setEditedBook(null)}
                className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-700 transition duration-300 ml-2"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ViewBooks;
