import React, { useEffect, useState } from 'react';
import './UserDashboard.css'; // Add some custom CSS for styling

const UserDashboard = () => {
  const [publishers, setPublishers] = useState([]);

  useEffect(() => {
    const fetchPublishers = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/auth/publishers');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setPublishers(data);
      } catch (error) {
        console.error('Failed to fetch publishers:', error);
      }
    };

    fetchPublishers();
  }, []);

  return (
    <div className="user-dashboard">
      {publishers.map((publisher) => (
        <div key={publisher._id} className="publisher-card">
          <h2>{publisher.publisherName}</h2>
          {publisher.authors.map((author) => (
            <div key={author._id} className="author-section">
              <h3>Author: {author.authorName}</h3>
              <div className="books-container">
                {author.books.map((book) => (
                  <div key={book._id} className="book-card">
                    <img src={book.imgUrl} alt={book.bookName} className="book-img" />
                    <h4>{book.bookName}</h4>
                    <p>{book.description}</p>
                    <p>Published on: {new Date(book.publisherDate).toLocaleDateString()}</p>
                    <p>Total Copies: {book.totalCopies}</p>
                    <p>Purchased Copies: {book.purchasedCopies}</p>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default UserDashboard;
