import React, { useState, useEffect } from 'react';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null);
  const [newBook, setNewBook] = useState({
    bookName: '',
    authorName: '',
    publisherName: '',
    imgUrl: '',
    description: '',
    publisherDate: '',
    totalCopies: 0
  });

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/auth/users');
        if (response.ok) {
          const data = await response.json();
          const filteredUsers = data.filter(user => user.role !== 'admin');
          setUsers(filteredUsers);
        } else {
          console.error('Failed to fetch users');
        }
      } catch (error) {
        console.error('Failed to fetch users:', error);
      }
    };

    fetchUsers();
  }, []);

  const handleEditUser = (user) => {
    setEditingUser(user);
  };

  const handleDeleteUser = async (userId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/auth/users/${userId}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        setUsers(users.filter(user => user._id !== userId));
      } else {
        console.error('Failed to delete user');
      }
    } catch (error) {
      console.error('Failed to delete user:', error);
    }
  };

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/api/auth/users/${editingUser._id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(editingUser),
      });
      if (response.ok) {
        const updatedUser = await response.json();
        setUsers(users.map(user => (user._id === editingUser._id ? updatedUser : user)));
        setEditingUser(null);
      } else {
        console.error('Failed to update user');
      }
    } catch (error) {
      console.error('Failed to update user:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewBook(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleAddBook = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/auth/books', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newBook),
      });
      if (response.ok) {
        alert('Book added successfully');
        setNewBook({
          bookName: '',
          authorName: '',
          publisherName: '',
          imgUrl: '',
          description: '',
          publisherDate: '',
          totalCopies: 0
        });
      } else {
        console.error('Failed to add book');
      }
    } catch (error) {
      console.error('Failed to add book:', error);
    }
  };

  const handleLogout = () => {
    // Redirect to the login page
    window.location.href = '/login';
  };

  const handleViewBooks = () => {
    // Navigate to view books page
    window.location.href = '/view-books';
  };

  return (
    <div className="admin-dashboard">
      <h1>Welcome to Admin Dashboard</h1>

      <button className="logout-button" onClick={handleLogout}>Logout</button>
      <button className="view-books-button" onClick={handleViewBooks}>View Books</button>

      <h2>User List</h2>
      <table className="user-table">
        <thead>
          <tr>
            <th>Full Name</th>
            <th>Username</th>
            <th>Email</th>
            <th>Phone Number</th>
            <th>Login Date</th>
            <th>Login Time</th>
            <th>Logout Time</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user._id}>
              <td>{user.fullName}</td>
              <td>{user.username}</td>
              <td>{user.email}</td>
              <td>{user.mobile}</td>
              <td>{user.lastLogin ? new Date(user.lastLogin).toLocaleDateString() : 'N/A'}</td>
              <td>{user.loginTime ? user.loginTime : 'N/A'}</td>
              <td>{user.logoutTime ? user.logoutTime : 'N/A'}</td>
              <td>
                <button onClick={() => handleEditUser(user)}>Edit</button>
                <button onClick={() => handleDeleteUser(user._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {editingUser && (
        <form className="edit-user-form" onSubmit={handleEditSubmit}>
          <h3>Edit User</h3>
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={editingUser.username}
            onChange={(e) => setEditingUser({ ...editingUser, username: e.target.value })}
            required
          />
          <input
            type="text"
            name="loginTime"
            placeholder="Login Time"
            value={editingUser.loginTime}
            onChange={(e) => setEditingUser({ ...editingUser, loginTime: e.target.value })}
            required
          />
          <input
            type="text"
            name="logoutTime"
            placeholder="Logout Time"
            value={editingUser.logoutTime}
            onChange={(e) => setEditingUser({ ...editingUser, logoutTime: e.target.value })}
            required
          />
          <button type="submit">Save</button>
          <button type="button" onClick={() => setEditingUser(null)}>Cancel</button>
        </form>
      )}

      <div className="add-book-form">
        <h2>Add New Book</h2>
        <form onSubmit={handleAddBook}>
          <input
            type="text"
            name="bookName"
            placeholder="Book Name"
            value={newBook.bookName}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="authorName"
            placeholder="Author Name"
            value={newBook.authorName}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="publisherName"
            placeholder="Publisher Name"
            value={newBook.publisherName}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="imgUrl"
            placeholder="Image URL"
            value={newBook.imgUrl}
            onChange={handleInputChange}
            required
          />
          <textarea
            name="description"
            placeholder="Description"
            value={newBook.description}
            onChange={handleInputChange}
            required
          />
          <input
            type="date"
            name="publisherDate"
            value={newBook.publisherDate}
            onChange={handleInputChange}
            required
          />
          <input
            type="number"
            name="totalCopies"
            placeholder="Total Copies"
            value={newBook.totalCopies}
            onChange={handleInputChange}
            required
          />
          <button type="submit">Add Book</button>
        </form>
      </div>
    </div>
  );
};

export default AdminDashboard;
