import React, { useEffect, useState } from 'react';

const Wishlist = () => {
  const [wishlist, setWishlist] = useState([]);

  useEffect(() => {
    const storedWishlist = JSON.parse(localStorage.getItem('favorites')) || [];
    setWishlist(storedWishlist);
  }, []);

  const handleRemoveFromWishlist = (book) => {
    const updatedWishlist = wishlist.filter(item => item._id !== book._id);
    setWishlist(updatedWishlist);
    localStorage.setItem('favorites', JSON.stringify(updatedWishlist));
  };

  const handleBuyNow = async (book) => {
    try {
      const response = await fetch(`http://localhost:5000/api/auth/books/purchase/${book._id}`, {
        method: 'POST'
      });
      if (response.ok) {
        alert(`Book purchased: ${book.bookName}`);
        handleRemoveFromWishlist(book); // Remove from wishlist after purchase
      } else {
        console.error('Failed to purchase book');
      }
    } catch (error) {
      console.error('Failed to purchase book:', error);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-3xl font-bold mb-6 text-indigo-600">My Wishlist</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {wishlist.map((book) => (
          <div
            key={book._id}
            className="bg-white shadow-lg rounded-lg p-4 flex flex-col items-center h-full transition-transform transform hover:scale-105 hover:shadow-2xl"
          >
            <img
              src={book.bookImgUrl}
              alt={book.bookName}
              className="h-40 w-full object-contain mb-4 rounded"
            />
            <div className="flex-grow flex flex-col justify-between w-full">
              <div className="text-center">
                <h4 className="font-semibold text-lg mb-2 text-indigo-700">{book.bookName}</h4>
                <p className="text-gray-600 mb-1">{book.authorName}</p>
                <p className="text-gray-600 mb-1">{new Date(book.publishingDate).toLocaleDateString()}</p>
                <p className="text-gray-600 mb-1">No. of Copies: {book.noOfCopies}</p>
              </div>
              <div className="flex justify-between mt-4 w-full">
                <button
                  onClick={() => handleBuyNow(book)}
                  className="bg-green-500 text-white w-1/2 py-1 rounded hover:bg-green-700 transition duration-300 mr-1"
                >
                  Buy Now
                </button>
                <button
                  onClick={() => handleRemoveFromWishlist(book)}
                  className="bg-red-500 text-white w-1/2 py-1 rounded hover:bg-red-700 transition duration-300 ml-1"
                >
                  Remove
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Wishlist;
