import React, { useEffect, useState } from 'react';

const ViewBooks = () => {
  const [purchasedBooks, setPurchasedBooks] = useState([]);

  useEffect(() => {
    const fetchPurchasedBooks = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/auth/purchased-books');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setPurchasedBooks(data);
      } catch (error) {
        console.error('Failed to fetch purchased books:', error);
      }
    };

    fetchPurchasedBooks();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-3xl font-bold mb-6 text-indigo-600">Purchased Books</h2>
      <table className="min-w-full bg-white border border-gray-300">
        <thead>
          <tr>
            <th className="py-2 px-4 border-b">Image</th>
            <th className="py-2 px-4 border-b">Book Name</th>
            <th className="py-2 px-4 border-b">Publication Date</th>
            <th className="py-2 px-4 border-b">Total Copies</th>
            <th className="py-2 px-4 border-b">Purchased Copies</th>
          </tr>
        </thead>
        <tbody>
          {purchasedBooks.map(book => (
            <tr key={book._id}>
              <td className="py-2 px-4 border-b">
                <img src={book.imgUrl} alt={book.bookName} className="h-16 w-16 object-contain" />
              </td>
              <td className="py-2 px-4 border-b">{book.bookName}</td>
              <td className="py-2 px-4 border-b">{new Date(book.publisherDate).toLocaleDateString()}</td>
              <td className="py-2 px-4 border-b">{book.totalCopies}</td>
              <td className="py-2 px-4 border-b">{book.purchasedCopies}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewBooks;
