import React, { useState, useEffect } from 'react';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [books, setBooks] = useState([]);
  const [formData, setFormData] = useState({
    publisherName: '',
    authorName: '',
    bookName: '',
    numOfCopiesAvailable: 0,
    publicationDate: '',
    imgUrl: '',
    description: '',
  });

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/books');
        if (response.ok) {
          const data = await response.json();
          setBooks(data);
        } else {
          console.error('Failed to fetch books');
        }
      } catch (error) {
        console.error('Failed to fetch books:', error);
      }
    };

    fetchBooks();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Form data to be sent:', formData);
    try {
      const response = await fetch('http://localhost:5000/api/books', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });
      if (response.ok) {
        const newBook = await response.json();
        console.log('New book added:', newBook);
        setBooks([...books, newBook.book]);
        alert('Book added successfully');
        setFormData({
          publisherName: '',
          authorName: '',
          bookName: '',
          numOfCopiesAvailable: 0,
          publicationDate: '',
          imgUrl: '',
          description: '',
        });
      } else {
        console.error('Failed to add book');
      }
    } catch (error) {
      console.error('Failed to add book:', error);
    }
  };

  return (
    <div className="admin-dashboard">
      <h1>Welcome to Admin Dashboard</h1>

      <form onSubmit={handleSubmit}>
        <h2>Add Book</h2>
        <input
          type="text"
          name="publisherName"
          placeholder="Publisher Name"
          value={formData.publisherName}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="authorName"
          placeholder="Author Name"
          value={formData.authorName}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="bookName"
          placeholder="Book Name"
          value={formData.bookName}
          onChange={handleInputChange}
          required
        />
        <input
          type="number"
          name="numOfCopiesAvailable"
          placeholder="Number of Copies Available"
          value={formData.numOfCopiesAvailable}
          onChange={handleInputChange}
          required
        />
        <input
          type="date"
          name="publicationDate"
          placeholder="Publication Date"
          value={formData.publicationDate}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="imgUrl"
          placeholder="Image URL"
          value={formData.imgUrl}
          onChange={handleInputChange}
        />
        <textarea
          name="description"
          placeholder="Description"
          value={formData.description}
          onChange={handleInputChange}
        ></textarea>
        <button type="submit">Add Book</button>
      </form>

      <h2>Books List</h2>
      <div className="books-list">
        {books.map((publisher) => (
          <div key={publisher._id} className="book-card">
            <div>
              <h3>Publisher: {publisher.publisherName}</h3>
              {publisher.authors.map((author) => (
                <div key={author._id}>
                  <h4>Author: {author.authorName}</h4>
                  {author.books.map((b) => (
                    <div key={b._id} className="book-details">
                      <h5>Book Name: {b.bookName}</h5>
                      <p>Copies Available: {b.numOfCopiesAvailable}</p>
                      <p>Publication Date: {new Date(b.publicationDate).toLocaleDateString()}</p>
                      <p>Description: {b.description}</p>
                      <img src={b.imgUrl} alt={b.bookName} />
                    </div>
                  ))}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminDashboard;
