import React, { useEffect, useState } from 'react';

const ViewBooks = () => {
  const [purchasedBooks, setPurchasedBooks] = useState([]);

  useEffect(() => {
    const fetchPurchasedBooks = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/auth/purchased-books');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setPurchasedBooks(data);
      } catch (error) {
        console.error('Failed to fetch purchased books:', error);
      }
    };

    fetchPurchasedBooks();
  }, []);

  // Group books by publisher and author
  const groupedBooks = purchasedBooks.reduce((acc, book) => {
    const publisher = acc[book.publisherName] || {};
    const author = publisher[book.authorName] || [];

    author.push(book);

    publisher[book.authorName] = author;
    acc[book.publisherName] = publisher;

    return acc;
  }, {});

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-3xl font-bold mb-6 text-indigo-600">Purchased Books</h2>
      <div className="overflow-x-auto">
        <table className="min-w-full bg-gray-800 text-white border border-gray-600">
          <thead>
            <tr>
              <th className="py-3 px-6 border-b-2 border-gray-600">Image</th>
              <th className="py-3 px-6 border-b-2 border-gray-600">Book Name</th>
              <th className="py-3 px-6 border-b-2 border-gray-600">Publication Date</th>
              <th className="py-3 px-6 border-b-2 border-gray-600">Total Copies</th>
              <th className="py-3 px-6 border-b-2 border-gray-600">Purchased Copies</th>
              <th className="py-3 px-6 border-b-2 border-gray-600">Author</th>
              <th className="py-3 px-6 border-b-2 border-gray-600">Publisher</th>
            </tr>
          </thead>
          <tbody>
            {Object.entries(groupedBooks).map(([publisherName, authors]) => (
              Object.entries(authors).map(([authorName, books]) => (
                books.map((book, index) => (
                  <tr key={book._id} className="hover:bg-gray-700">
                    <td className="py-3 px-6 border-b border-gray-600">
                      <img src={book.imgUrl} alt={book.bookName} className="h-16 w-16 object-contain" />
                    </td>
                    <td className="py-3 px-6 border-b border-gray-600">{book.bookName}</td>
                    <td className="py-3 px-6 border-b border-gray-600">{new Date(book.publisherDate).toLocaleDateString()}</td>
                    <td className="py-3 px-6 border-b border-gray-600">{book.totalCopies}</td>
                    <td className="py-3 px-6 border-b border-gray-600">{book.purchasedCopies}</td>
                    {index === 0 && (
                      <>
                        <td className="py-3 px-6 border-b border-gray-600" rowSpan={books.length}>{authorName}</td>
                        <td className="py-3 px-6 border-b border-gray-600" rowSpan={books.length}>{publisherName}</td>
                      </>
                    )}
                  </tr>
                ))
              ))
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ViewBooks;
