import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';


const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null);
  const [showAddBookForm, setShowAddBookForm] = useState(false);

  const [book, setBook] = useState({
    bookName: '',
    authorName: '',
    publisherName: '',
    publisherDate: '',
    totalCopies: '',
    imgUrl: '',
    description: ''
  });

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/auth/users');
        if (response.ok) {
          const data = await response.json();
          const filteredUsers = data.filter(user => user.role !== 'admin');
          setUsers(filteredUsers);
        } else {
          console.error('Failed to fetch users');
        }
      } catch (error) {
        console.error('Failed to fetch users:', error);
      }
    };

    fetchUsers();
  }, []);

  const handleAddBook = async (e) => {
    e.preventDefault();

    const bookDetails = {
      bookName: book.bookName,
      imgUrl: book.imgUrl,
      description: book.description,
      publisherDate: new Date(book.publisherDate),
      totalCopies: parseInt(book.totalCopies),
      purchasedCopies: 0,
    };

    const data = {
      publisherName: book.publisherName,
      authorName: book.authorName,
      bookDetails,
    };

    try {
      const response = await fetch('http://localhost:5000/api/auth/books', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });
      if (response.ok) {
        alert('Book added successfully');
        setBook({
          bookName: '',
          authorName: '',
          publisherName: '',
          publisherDate: '',
          totalCopies: '',
          imgUrl: '',
          description: ''
        });
        setShowAddBookForm(false); // Hide the form after adding the book
      } else {
        console.error('Failed to add book');
      }
    } catch (error) {
      console.error('Failed to add book:', error);
    }
  };

  const handleBookChange = (e) => {
    const { name, value } = e.target;
    setBook({ ...book, [name]: value });
  };

  const handleLogout = () => {
    window.location.href = '/login';
  };

  const navigate = useNavigate();

  const handleViewBooks = () => {
    navigate('/view-books');
  };

  const handleEditUser = (user) => {
    setEditingUser(user);
  };

  const handleDeleteUser = async (userId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/auth/users/${userId}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        setUsers(users.filter(user => user._id !== userId));
      } else {
        console.error('Failed to delete user');
      }
    } catch (error) {
      console.error('Failed to delete user:', error);
    }
  };
  /*const handleDeleteUser = async (userId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/auth/users/${userId}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        setUsers(users.filter(user => user._id !== userId));
      } else {
        console.error('Failed to delete user');
      }
    } catch (error) {
      console.error('Failed to delete user:', error);
    }
  };/*
  

  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/api/auth/users/${editingUser._id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(editingUser),
      });
      if (response.ok) {
        const updatedUser = await response.json();
        setUsers(users.map(user => (user._id === editingUser._id ? updatedUser : user)));
        setEditingUser(null);
      } else {
        console.error('Failed to update user');
      }
    } catch (error) {
      console.error('Failed to update user:', error);
    }
  };
 /*const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/api/auth/users/${editingUser._id}`, {
        method: 'PUT',
        headers: {
